package jetjava;

public class variable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 11; //local variable declared inside the class
		int b = 20; //local variable declared inside the class
		int sum;	//local variable declared inside the class
		sum = a + b; // access variables inside the method 
		System.out.println(sum);
	}

}